import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      firstNumber: '',
      secondNumber: '',
      operation: '',
      result: '',
      firstNumberSaved: '',
      secondNumberSaved: ''
    }
  };

  changeNumber = (number) => {
    let newNumber
    if (this.state.operation) {
      newNumber = number + this.state.firstNumber
      this.setState({
        firstNumber: newNumber
      });
    } else {
      newNumber = number + this.state.secondNumber
      this.setState({
        number: newNumber
      });
    }
  };

  render() {
    const firstNumber = this.state.firstNumber;
    const secondNumber = this.state.secondNumber;
    const operation = this.state.operation;
    const result = this.state.result;
    let number
    if (operation) {
      number = <h5>{secondNumber}</h5>
    } else if (!operation) {
      number = <h5>{firstNumber}</h5>
    } else if (result) {
      number = <h5>{result}</h5>
    } else {
      number = ""
    }
    return (
      <div>
        <div className="title">
          <h5>React Calculator - Chris Yang</h5>
        </div>
        <div className = "output">
          <h5>{number}</h5>
        </div>
      <div className="calculatorBody">
        <div className="row" id="calcRow">
          <div className="col s4 m4">
            <a onClick={() => this.changeNumber(7)}>&nbsp;7&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;8&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;9&nbsp;</a>
          </div>
        </div>
        <div className="row" id="calcRow">
          <div className="col s4 m4">
            <a>&nbsp;4&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;5&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;6&nbsp;</a>
          </div>
        </div>
        <div className="row" id="calcRow">
          <div className="col s4 m4">
            <a id="smallbutton">&nbsp;1&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;2&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>&nbsp;3&nbsp;</a>
          </div>
        </div>
        <div className="row" id="calcRow">
          <div className="col s4 m4">
            <a>&nbsp;0&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a id="smallbutton">&nbsp;.&nbsp;</a>
          </div>
          <div className="col s4 m4">
            <a>AC</a>
          </div>
        </div>
      </div>
      <div className="operators">
      <div className="row" id="calcRow">
        <div className="col s3 m3">
          <a>&nbsp;&#43;&nbsp;</a>
        </div>
        <div className="col s3 m3">
          <a>&nbsp;&#8722;&nbsp;</a>
        </div>
        <div className="col s3 m3">
          <a>&nbsp;&#215;&nbsp;</a>
        </div>
        <div className="col s3 m3">
          <a>&nbsp;&#247;&nbsp;</a>
        </div>
      </div>
    </div>
      </div>
    )
  }

};

export default App;
